'use strict';

function main() {
  console.log('es2015 goodies, yo');

  getPages().then(function (pages) {
    pages.map(function (page) {
      console.log('page number ' + page);
    });
  });
}

function getPages() {
  return new Promise(function (resolve, reject) {
    resolve([1, 2, 3]);
  });
}

main();